    public List<Noticia>  getXmlUOL(){
        try {
            List<Noticia> noticias = new ArrayList<>();
            String url = "http://rss.uol.com.br/feed/economia.xml";
            try (XmlReader reader = new XmlReader(new URL(url))) {
                SyndFeed feed = new SyndFeedInput().build(reader);

                for (SyndEntry entry : feed.getEntries()) {
                    Noticia noticia = new Noticia();
                    noticia.setTitulo(entry.getTitleEx().getValue());
                    noticia.setDescricao(entry.getDescription().getValue());
                    noticia.setLink(entry.getLink());
                    noticia.setEmissora("UOL");
                    noticia.setDtNoticia(entry.getPublishedDate());
                    noticia.setDtNoticiaFormatado(_dateutil.formDate(entry.getPublishedDate()));
                    noticia.setFoto("".getBytes(StandardCharsets.UTF_8));
                    noticias.add(noticia);
                }
                List<Noticia> noticiaList = _noticiaRepository.saveAll(noticias);
                ZupLog log = new ZupLog();
                log.setDescricao("Tarefa Uol executada com sucesso");
                log.setDt_entrada(
                        _dateutil.formLocalDate(LocalDateTime.now())
                );
                _zupLogRepository.save(log);
                return noticiaList;
            }
        }  catch (Exception e) {
            e.printStackTrace();
        }
        throw new ResponseStatusException(HttpStatus.NOT_FOUND, "Notícia não encontrado");
    }
