    public Noticia procuraPorNome(Gpt gpt){
        Optional<Noticia> noticia = _noticiaRepository.findByTitulo(gpt.getTitulo());
        if (noticia.isPresent()) {
            return noticia.get();
        }
        throw new ResponseStatusException(HttpStatus.NOT_FOUND, "Notícia não encontrado");
    }
    public Comentario buscarNoticiaPorIdComentario(ComentarioRequest comentario, int idNoticia, Long idUsuario){
        List<Comentario> comentarios = _comentarioRepository.findFirstCommentWithLimit(idUsuario, idNoticia);
        Optional<Noticia> noticias = _noticiaRepository.findById(idNoticia);
        if (noticias.isPresent()){
            if (comentarios.size() == 0){
                var buscaUsuario = _usuarioService.buscaPorId(idUsuario);

                Comentario criarComentario = new Comentario().builder()
                        .descricao(comentario.getComentario())
                        .usuario(buscaUsuario)
                        .noticias(noticias.get())
                        .build();
                _comentarioRepository.save(criarComentario);
                return criarComentario;
            } else{
                Comentario criarComentario = new Comentario().builder()
                        .descricao(comentario.getComentario())
                        .usuario(comentarios.get(0).getUsuario())
                        .noticias(noticias.get())
                        .build();
                _comentarioRepository.save(criarComentario);
                return criarComentario;
            }
        }
        throw new ResponseStatusException(HttpStatus.NOT_FOUND, "Notícia não encontrado");
    }
    public Curtida buscarNoticiaPorIdLikes(LikesRequest like, Long idUsuario, int idNoticia){
        Curtida curtida = _curtidaRepository.findFirstLikeWithLimit(idUsuario, idNoticia);
            if (curtida == null){
                var buscaUsuario = _usuarioService.buscaPorId(idUsuario);
                Optional<Noticia> noticias = _noticiaRepository.findById(idNoticia);
                if (noticias.isPresent()){
                    Curtida novaCurtida = new Curtida().builder()
                            .likes(like.getLikes())
                            .usuario(buscaUsuario)
                            .noticias(noticias.get())
                            .build();
                    _curtidaRepository.save(novaCurtida);
                }
                throw new ResponseStatusException(HttpStatus.NOT_FOUND, "Notícia não encontrado");
            } else {
                if (curtida.getLikes() < 1){
                    Curtida novaCurtida = new Curtida().builder()
                            .id(curtida.getId())
                            .likes(like.getLikes())
                            .usuario(curtida.getUsuario())
                            .noticias(curtida.getNoticias())
                            .build();
                    _curtidaRepository.save(novaCurtida);
                }
                else if (curtida.getLikes() == 1){
                    Curtida novaCurtida = new Curtida().builder()
                            .id(curtida.getId())
                            .likes(0)
                            .usuario(curtida.getUsuario())
                            .noticias(curtida.getNoticias())
                            .build();
                    _curtidaRepository.save(novaCurtida);
                }
            }
            return curtida;
    }
    public List<Comentario> comentarios(){
        var retorno = _comentarioRepository.findAll();
        if (retorno.isEmpty()){
            return null;
        }
        return retorno;
    }
    public List<ComentarioResponse> getComentarioNoticiaPorId(Noticia noticia){
        List<Comentario> comentarios = _comentarioRepository.findAll();
        List<ComentarioResponse> listaComentariosResponse = new ArrayList();
        if (!comentarios.isEmpty()){
            for (int i = 0; i < comentarios.size(); i++){
                if (comentarios.get(i).getNoticias().getId() == noticia.getId()){
                    ComentarioResponse comentarioResposta = new ComentarioResponse();
                    comentarioResposta.setId(comentarios.get(i).getId());
                    comentarioResposta.setUsuario(new UsuarioResponse(comentarios.get(i).getUsuario()));
                    comentarioResposta.setDescricao(comentarios.get(i).getDescricao());
                    listaComentariosResponse.add(comentarioResposta);
                }
            }
            return listaComentariosResponse;
        }
        return null;
    }
